changeSet.sql:
CREATE EXTENSION IF NOT EXISTS "uuid-ossp";
CREATE SCHEMA IF NOT EXISTS "users";
CREATE TABLE IF NOT EXISTS "users"."user" ( id UUID
default uuid_generate_v4 ( ) NOT NULL , first_name TEXT, last_name TEXT, phone TEXT, deleted BOOLEAN DEFAULT FALSE, created_date TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP, last_modified_date TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP, version BIGINT );
